import React from 'react'
import { NextPage } from 'next';
import fetch from 'isomorphic-unfetch';
import { Layout } from '../components/Layout';
import useSWR from 'swr'
import axios from 'axios'
import Markdown from 'react-markdown';

const API_URL = 'https://hndup-api-cms.herokuapp.com/graphql'
interface Props{
    userAgent?: string

}

const fetcher = async () => {
    const res = await axios.post(API_URL,{
        query: `      
        {
            restaurants {
              Name
              categories{
                id
                name
              }
              picture {
                url
              }
              Description
            }
         }
    `,
    },{ headers: {
        'Content-Type': 'application/json'
    }})
    const json =  res
    console.log(json)
    return json.data.restaurants
}

const HomePage: NextPage<Props> = ({ userAgent }) => {
    const { data, error } = useSWR('/repos/zeit/next.js', fetcher)
    console.log(data)
    return (
        <Layout>
            {
                data && data.map(restaurant => (
                    <div key={restaurant.Name}>
                        <h1>Name: {restaurant.Name}</h1>
                        <img src={restaurant.picture.url} 
                            alt=""
                            width={50}
                            height={50}
                        ></img>
                       <Markdown
                            escapeHtml={true}
                            source={restaurant.Description}
                       />
                    </div>
                ))
            }
        </Layout>
    )
}


export default HomePage
