import fetch from 'isomorphic-unfetch'
const api = 'https://nameless-crag-20139.herokuapp.com/graphql'

const body  =  `{
    restaurants {
      Name
      categories{
        id
        name
      }
      picture {
        url
      }
    }
  }`


export const getRestaurants = () =>  {
   return fetch(`${api}`, { 
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        },
        body
     })
    .then(res => res.json())
    .then(data => data.restaurants)
}
