import React from 'react'
import { NextPage } from 'next';
import Link from'next/link';
import { Layout } from '../components/Layout';
interface Props {
    userAgent?: string
    data: any
}

const HomePage: NextPage<Props> = ({ userAgent,data }) => {
    return (
        <Layout>
            {data && data.map((restaurant:any) => (
                <div>
                    <h1>Name: {restaurant.name} </h1>
                    <img width={50} height={50} src={`https://nameless-crag-20139.herokuapp.com${restaurant.picture.url}`}>
                    </img>
                    <Link href="/about">Go to About Page</Link>
                </div>
            ))
            }
        </Layout>
    )
}

HomePage.getInitialProps = async () => {
    const res = await fetch('https://nameless-crag-20139.herokuapp.com/graphql',{
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        },
        body: `{
            restaurants {
              Name
              categories{
                id
                name
              }
              picture {
                url
              }
            }
          }`
    })
    const data = await res.json()
    return {
        data: data.restaurants
    }
  };

export default HomePage
