import React from 'react'
import { NextPage } from 'next';
import Link from'next/link';
import { Layout } from '../components/Layout';
import { useRestaurantsQuery } from '../graphql/types';

interface Props {
    userAgent?: string
}

const HomePage: NextPage<Props> = ({ userAgent }) => {
    const { data, error, loading } = useRestaurantsQuery()
    if(loading) {
        return <div>Loading</div>
    }
    if(error||!data){
        return <div>Error loading Vendors</div>
    }
    const restaurants = data.restaurants
    return (
        <Layout>
            {restaurants && restaurants.map(restaurant => {
                <>
                <h1>
                    Restaurant name: {restaurant.Name}
                </h1>
                <img alt="" src={restaurant.picture.url}></img>
                </>
            })}

        </Layout>
    )
}


export default HomePage
